SYSTEM_INSTRUCTION = f"""
You are an expert script assistant for Agatha Christie's "And Then There Were None" (Samuel French Acting Edition).
Use the following script text to answer questions:
{SCRIPT_CONTEXT}

RULES:
1. You have the full context of the Cast, Setting, and Dialogue.
2. Note that this script has TWO endings: the 1943 Play Ending (Happy) and the 1939 Novel Ending (Dark).
3. If a user asks about the ending, clarify which one they mean or explain both.
4. Keep answers helpful for actors and directors.
"""

# --- 4. CHAT LOGIC ---

# Initialize chat history
if "messages" not in st.session_state:
    st.session_state.messages = []

# Display chat messages from history on app rerun
for message in st.session_state.messages:
    with st.chat_message(message["role"]):
        st.markdown(message["content"])

# Accept user input
if prompt := st.chat_input("Ex: What happens to Dr. Armstrong?"):
    # Add user message to chat history
    st.session_state.messages.append({"role": "user", "content": prompt})
    
    # Display user message in chat message container
    with st.chat_message("user"):
        st.markdown(prompt)

    # Display assistant response in chat message container
    with st.chat_message("assistant"):
        message_placeholder = st.empty()
        full_response = ""
        
        try:
            # Try this first (The stable version)
            model = genai.GenerativeModel('gemini-1.5-flash-001')
            
            # Construct the chat history for Gemini
            # We add the System Instruction as the first part of the prompt context
            chat = model.start_chat(history=[])
            
            # We send the system instruction + user prompt
            # (For simple bots, sending the context in the first prompt is easiest)
            response = model.generate_content(
                SYSTEM_INSTRUCTION + "\n\nUser Question: " + prompt
            )
            
            full_response = response.text
            message_placeholder.markdown(full_response)
            
        except Exception as e:
            full_response = f"Error: {str(e)}"
            message_placeholder.error(full_response)

    # Add assistant response to chat history
    st.session_state.messages.append({"role": "assistant", "content": full_response})
